(72, '<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp;</div>\r\n<table align="center" id="table264" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体" size="2">2006年11月20日，公司投资项目&ldquo;中材科技股份有限公司&rdquo;在深圳证券交易所中小板挂牌上市（简称&ldquo;中材科技&rdquo;，代码：002080），当日收报14.83元，较8.98元的发行价上涨65％。</font> </span>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年11月3日，深圳市国有资产监督管理委员会在麒麟山庄召开专家论证会，论证公司发展战略规划（2006－2010年）。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年10月25日，公司投资项目&ldquo;慧视通讯科技有限公司&rdquo;在澳大利亚证券交易所（代码：ARR）上市。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年10月13日，公司在五洲宾馆举办第八届高交会&ldquo;创业投资合作项目签约仪式&rdquo;，深圳市常务副市长刘应力等到会祝贺。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年7月28日，公司投资项目&ldquo;上海华拓医药科技发展股份有限公司&rdquo;在&ldquo;中国浦东股权交易信息公示中心&rdquo;挂牌。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年7月27至30日，公司召开2006年度年中工作会议。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年7月，公司当选&ldquo;中国最具发展潜力企业&rdquo;</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年6月27日，公司投资项目&ldquo;深圳市同洲电子股份有限公司&rdquo;在深圳证券交易所中小板上市（简称&ldquo;同洲电子&rdquo;，代码：002052），当日收报35.63元，较16.00元的发行价上涨123％。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年6月1日，公司投资项目&ldquo;东方纪元有限公司&rdquo;在新加坡证券交易所（代码：ORIENTCENT）上市。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年5月 25日，公司在&ldquo;第二届亚太投资峰会暨Top Capital颁奖典礼&rdquo;上荣获&ldquo;最得意本土VC奖&rdquo;。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年 5月16日，日本前劳动大臣、前经济规划厅厅长近藤铁雄率领日本创业投资代表团访问我司。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年4月6日，公司在&ldquo;2006（第八届）中国风险投资论坛&rdquo;上再获&ldquo;最佳风险投资机构奖&rdquo;和&ldquo;最佳投资奖&rdquo;。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年3月9至10日，公司召开2006年工作会议。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年1月18日，公司中层干部竞聘上岗、员工双向选择等劳动人事分配制度改革试点工作顺利完成。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2006年1月5日，公司召开第十六次股东会暨三届一次董事会、三届一次监事会，选举产生了新一届董事会、监事会，聘任了新一届经营班子。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></div>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(73, '<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp;</div>\r\n<table align="center" id="table264" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体" size="2">2005年12月，靳海涛董事长参加&ldquo;2005以色列环球商务会议&rdquo;，并作为特邀嘉宾发表主旨演讲。会议期间，靳海涛董事长拜会了时任以色列总理沙龙、副总理奥尔默特、沙洛姆、前总理巴拉克和中国驻以色列大使陈永龙等知名人士。</font> </span>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年11月30日，公司投资项目&ldquo;珠海炬力集成电路&rdquo;在美国纳斯达克(NASDAQ: ACTS)上市。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年11月 6日，公司参加了中国会计学会在厦门国家会计学院召开的&ldquo;中国创业投资企业会计准则专题研讨会&rdquo;。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年10月15日，第七届高交会期间，公司与科技部火炬中心、深圳证券交易所、深圳市创业投资同业公会、深圳市投资商会共同举办&ldquo;股权分置改革与创业投资发展新机遇高层论坛&rdquo;。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年10月15日，公司在深圳五洲宾馆深圳厅举行中以基金、中韩基金等合作项目签约仪式，深圳市副市长张思平等到会祝贺。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年8月5日，公司召开2005年上半年工作会议。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年5月6日，公司投资项目&ldquo;德信无线&rdquo;在美国纳斯达克（NASDAQ：CNTF）上市。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年4月12日，公司召开第十四次股东会暨二届六次董事会、二届八次监事会。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年4月11日，公司联合深圳市创业投资同业公会，邀请全球著名风险投资研究专家马丁博士举办风险投资专题讲座。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年4月9日，公司在&ldquo;2005（第七届）中国风险投资论坛&rdquo;上获颁&ldquo;最佳风险投资机构奖&rdquo;、&ldquo;最佳投资奖&rdquo;和&ldquo;最佳融资奖&rdquo;。靳海涛董事长在论坛上作题为《对当前创业投资战略对策的思考》的演讲。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年4月8日，科技部副部长刘燕华一行到公司调研，并参加公司与深圳市创业投资同业公会共同组织的科技创业投资座谈会。随同刘燕华副部长前来调研的还有广东省科技厅谢明权厅长、深圳市科技和信息局王有明副局长等。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年3月3--5日，公司召开了2005年经济工作会议。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>\r\n	<p><span id="lblContent"><font face="宋体" size="2">2005年2月25日，经美国制造商协会主席杰瑞先生和美国总统贸易与谈判政策委员会委员王亚平先生介绍和推荐，《他改变了中国：江泽民传》一书的作者&mdash;&mdash;罗伯特&middot;劳伦斯&middot;库恩博士来公司访问。</font></span></p>\r\n	</div>\r\n	<div><span id="lblContent">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></div>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(74, '<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp;</div>\r\n<table align="center" id="table264" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年12月13日，台湾创业投资之父、台湾全球策略投资基金董事长杨世缄先生到公司访问。</font></font></span><br />\r\n	<div><span id="lblContent"><font face="宋体"><font face="宋体" size="2">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></font></span></div>\r\n	<div><span id="lblContent"><font face="宋体"><font face="宋体" size="2">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2004年12月3日，公司投资项目&ldquo;九城数码&rdquo;在美国纳斯达克（NASDAQ：NINE）上市。</font></font></span></div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年10月22日，集团公司召开全体员工大会，深圳市创新投资集团有限公司党委成立大会。市国资委党委副书记柏立新代表市国资委党委做重要讲话，并宣布靳海涛董事长任公司党委书记。 </font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年10月14日，第六届高交会期间，集团公司举行&ldquo;合作项目签约及博士后工作站揭牌仪式&rdquo;。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年8月25日，集团公司召开第十三次股东会暨二届五次董事会、二届七次监事会，靳海涛当选为深圳市创新投资集团有限公司董事长。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年3月26日，集团公司召开第十二次股东会暨二届四次董事会、二届六次监事会。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体"><font face="宋体" size="2">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></font></span></div>\r\n	<div><span id="lblContent"><font face="宋体"><font face="宋体" size="2">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2004年3月17日，公司投资项目&ldquo;中芯国际&rdquo;在美国纽交所和香港交易所（NYSE：SMI, HKEX: 0981`）上市。</font></font></span>\r\n	<p>&nbsp;</p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年1月，在&ldquo;第三届中国创业投资年度论坛&rdquo;上，集团公司名列&ldquo;2003年中国本土创业投资机构历史排名30强&rdquo;第一名，&ldquo;2003年中国本土创业投资机构30强&rdquo;第三名。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年1月15－16日，集团公司召开2004年工作会议。孙俊平董事长就公司经营现状和发展作重要讲话，陈玮总裁作2004年公司工作报告。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2004年1月14日，集团公司召开2004年第一次临时股东会暨第一次临时董事会、第一次临时监事会。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span></div>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(75, '<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp;</div>\r\n<table align="center" id="table264" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2003年10月30日下午，张思平副市长率市国资办、市地税局、市投资管理公司等部门有关领导到集团公司检查指导工作。</font> </font></span>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2003年10月13－15日，公司参加第五届高交会，并成功举办&ldquo;创业投资与多层次资本市场&rdquo;论坛。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2003年9月19日，在科技部召开的全国高新技术产业化工作会议暨火炬计划十五周年总结表彰大会上，集团公司荣获&ldquo;火炬计划优秀创业投资机构&rdquo;称号。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2003年8月29日，集团公司在深圳召开&ldquo;深圳市创新投资集团有限公司成立四周年座谈会&rdquo;。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2003年8月28日，集团公司在深圳召开2003年第十一次股东会暨二届三次董事会、二届四次监事会。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font size="2"><font size="+0">2003年3月21日，集团公司在海南召开第十次股东会暨二届二次董事会、二届三次监事会。</font></font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体"><font size="2">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; </font></font></span>\r\n	<p>&nbsp;</p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span></div>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(76, '<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp;</div>\r\n<div>&nbsp;</div>\r\n<table align="center" id="table264" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2002年10月，创新投资集团诞生。</font> </font></span>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2002年1月，创新科技投资有限公司正式实施基金与基金管理运作模式，下属各投资管理公司共受托管理资金11.1亿元人民币。</font></font></span></p>\r\n	</div>\r\n	<div><span id="lblContent"><font face="宋体">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </font></span></div>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(78, '<table align="center" id="table264" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2000年12月19日，公司召开第五次股东大会，选举李黑虎为第二任董事长。</font> </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2000年10月12日，公司在五洲宾馆举行第二届高交会项目签约仪式。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2000年10月12日，全国人大副委员长成思危到公司视察指导工作。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2000年9月13日，阚治东总经理应邀出席&ldquo;2000亚洲科技风险投资论坛&rdquo;，并以&ldquo;中国风险投资现状与趋势&rdquo;为题发表演讲。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2000年7月1日至7日：李万寿、姜卫平副总经理赴新加坡洽谈合作，与新加坡国家科技局（NSTS）及大华银行（UOB）等机构达成合作意向。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">2000年1月19日，公司召开第二次股东会暨一届三次董事会，讨论通过了公司2000年工作报告、财务报告，对公司上一年度的经营表示满意。</font></font></span></p>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(79, '<table align="center" id="table252" style="font-size: 14px" width="80%">\r\n<tbody>\r\n	<tr>\r\n	<td style="font-size: 12px"><span id="lblContent"><font face="宋体"><font face="宋体" size="2">1999年10月6日，公司在五洲宾馆深圳厅举行联合签约仪式，签署了第一批七个项目投资协议和两个项目合作协议，协议金额7100万元。</font> </font></span>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">1999年9月29日，公司投资委员会召开第一次项目评审会。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">1999年8月26日，在五洲宾馆深圳厅召开了隆重的公司成立大会。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">1999年8月12日，在投资大厦18楼会议室召开了公司第一次股东会，通过《公司章程》，选举产生第一届董事会和监事会。王穗明任第一任董事长。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font face="宋体" size="2">1999年7月27日，在五洲宾馆泰山厅召开公司筹备会议。</font></font></span></p>\r\n	<p><span id="lblContent"><font face="宋体"><font size="2">1999年7月19日，庄心一副市长主持召开市长工作会议，研究我公司具体筹建事宜,解决了公司成立过程中的一系列实际问题。</font></font></span></p>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, 'show-sj1', 0, '', 0, 1, '|0'),
(80, '<p class="pjuli">\r\n					<span>\r\n					深圳市创新投资集团是以资本为主要联结纽带的母子公司为主体的大型投资企业集团，于2002年10月正式成立。集团核心企业——深圳市创新投资集团有限公司的前身为1999年8月26日成立的深圳市创新科技投资有限公司。\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>公司愿景</b>\r\n				</p>\r\n				<p class="pjuli">\r\n					<span>\r\n						成为具有国际竞争力的创业投资集团，跻身国际知名创业投资机构行列。\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>公司使命</b>\r\n				</p>\r\n				<p class="pjuli">\r\n					<span>\r\n						为股东实现价值最大化；为员工提供前景广阔的职业发展空间；探索具有中国特色的创业投资事业发展之路；为中国创业投资发展环境的改善做出贡献。\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>公司的核心价值观</b>\r\n				</p>\r\n				<p class="pjuli">\r\n					<span>\r\n					<b class="bdazi">诚笃、务实、创新、以人为本</b>\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>主营业务</b>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						创业投资\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						股权投资于增值服务\r\n					</span>\r\n				</p>\r\n				<p class="pjuli">\r\n					<span>\r\n						以创业投资业务为支柱，以创业投资相关增值服务业务为延伸，走基金与基金管理的发展之路，推动深圳乃至我国高新技术产业的发展。\r\n					</span>\r\n				</p>\r\n\r\n				<p>\r\n					<b>发展战略</b>\r\n				</p>\r\n				<p class="pjuli">\r\n					<span>\r\n						继续维持国内资本规模最大、投资能力最强、最具竞争力的内资创业投资公司“领头羊”的地位，努力发展成为具有国际竞争力的创业投资集团，跻身国际知名投资机构行列。\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>基金管理</b>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						区域性政府引导基金：分为中央、省级、地市、县市等四级 \r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						中外合作/ 合资基金： 两头在外（如中以基金） \r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span class="sjuli">\r\n						一头在外，一头在内（如中新基金）\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span class="sjuli">\r\n						两头在内（如红杉创新基金）\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						商 业 化 基 金：&nbsp;&nbsp;    管理费+业绩分成（如中山基金） \r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span class="sjuli">\r\n						业绩分成（如南京基金） \r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span class="sjuli">\r\n						 加盟管理公司（如康沃基金）\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span class="sjuli">\r\n						 信托计划创投基金（如深国投创新资本一号）\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span class="sjuli">\r\n						有限合伙基金（如融智创新基金）\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						战 略 合 作 基 金 ： 联合投资基金（如优势资本） \r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>经营业绩</b>\r\n				</p>\r\n				<p class="pjuli">\r\n					<span>\r\n						平均年投资回报率（IRR）为36%。\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>团队</b>\r\n				</p>\r\n				<p>\r\n					<b>专业素质</b>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						具备多年的科技行业、投资银行、创业投资等工作经验\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						专业投资小组——IT、生物医药、新材料、光机电、新能源、新材料\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						全中国第一家也是唯一的创业投资博士后工作站为我们的投资提供了理论支持和人才补充\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						拥有“洋脑”——在创业投资领域有着丰富经验的外籍高管(曾在知名国际企业任职)\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>高学历、高素质</b>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						投资经理均为硕士以上学历\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						40%博士和双硕士\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						30%具有海外(美国、欧洲、日本、澳洲、新加坡)留学与工作的经历\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<b>具备行业实践经验</b>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						2－11年创业投资项目管理经验\r\n					</span>\r\n				</p>\r\n				<p>\r\n					<span>\r\n						具有平均超过12年的行业工作经验\r\n					</span>\r\n				</p> ', 0, '', 0, 10000, '', 0, '', 0, 1, '|0'),
(81, '<p align="center"></p>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp;&nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp;\r\n<p>&nbsp;</p>\r\n</div>\r\n<br />\r\n', 0, '', 0, 10000, '', 0, '', 0, 1, '|0'),
(82, '<table align="center" border="0" cellpadding="0" cellspacing="0" width="95%">\r\n<tbody>\r\n	<tr valign="top">\r\n	<td>\r\n	<p><font size="2">&nbsp;&nbsp;&nbsp;&nbsp;十六载风雨兼程，十六载春华秋实。历经16年探索，深圳市创新投资集团全体员工秉承&ldquo;诚笃、务实、创新、以人为本&rdquo;的理念，率先建立科学高效的创业投资公司治理结构和风控体系，逐步发展成为国内领先、具有国际竞争力的创业投资集团，投资的企业在资本市场公开上市超过100家，产业布局遍布全国。<br />\r\n	&nbsp;&nbsp;&nbsp; 当前，受益于我国改革发展红利的不断释放，全民创业的热情正在被点燃，大众创业、万众创新的时代向我们走来。我国创投行业面临千载难逢的历史机遇，呈现百舸争流、千帆竞发的大好局面。机遇与挑战并存，风险与回报同在。我们对未来充满信心。<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;天道酬勤，厚德载物。我们相信，在全体股东和各界朋友的大力支持下，凭借公司多年来积累的人脉资源和业界口碑，我们这支敢于拼搏、能打硬仗、团结一心的员工团队，一定能在不久的将来创造新的辉煌，百尺竿头更进一步，以实实在在的业绩实现金融报国、产业强国之梦。</font></p>\r\n	</td>\r\n	<td>\r\n	<div align="center"><font size="2">&nbsp; <img alt="" height="310" src="statics/images/lyt/dsz.jpg" width="205" /></font></div>\r\n	</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n', 0, '', 0, 10000, '', 0, '', 0, 1, '|0'),
(83, '<table id="table1" cellspacing="0" cellpadding="0" width="95%" align="center" border="0" style="font-size: 14px">\r\n    <tbody>\r\n        <tr valign="top">\r\n            <td style="font-size: 12px">\r\n            <p style="line-height: 150%"><span style="font-size: small"><font size="2">&nbsp;&nbsp;&nbsp; 深创投诞生于深圳这片创新的热土，发端于创业投资这一创新的行业，肩负着支持高新技术产业、带动深圳创业投资发展的使命。从她诞生之日起，创新就深深融入到深创投成长的历程之中。从开创“一头在内，一头在外”的中外合作基金运行模式，到率先打造全国范围的政府引导基金网络；从创立投资企业俱乐部，到专业化投资和全方位服务并进；创新已成为深创投的灵魂，是不断创新让我们始终走在行业的最前列。<br>\r\n            &nbsp;&nbsp;&nbsp; 春华秋实。深创投十多年的发展，不仅创造了辉煌的业绩，也锤炼出近二百人的专业队伍，聚集了中国创投行业最早的一批精英。他们是投资家，拥有丰富的专业经验；他们是诊断师，为企业出谋划策；他们是培训师，成就了一批创业家的梦想。<br>\r\n            &nbsp;&nbsp;&nbsp; 展望未来，“中国梦”在昭示着中国的明天。在这个追逐梦想、实现梦想的伟大时代，为中小企业插上腾飞的翅膀、为创业家实现创业的愿望是我们捧在手中的“创投梦”。我们不仅投资，更愿与企业成为荣辱与共的伙伴，聚彼此智慧，扬各自所长，一起去谱写我们共同成长、更加辉煌的新篇章。<br>\r\n            </font></span></p>\r\n            </td>\r\n            <td width="196" style="font-size: 12px">\r\n            <div align="center"><img src="statics/images/lyt/zc.jpg" alt="">&nbsp;</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 0, '', 0, 10000, '', 0, '', 0, 1, '|0');
